import { useQuery, useSubscription } from "@apollo/react-hooks"
import gql from "graphql-tag";

const map = new WeakMap()


const GET_MESSAGES_SUB = gql`
  subscription {
    message {
      id
      text
    }
  }
`


export default function useLiveQuery(queryGql) {
  const queryResult = useQuery(queryGql)

  if (!map.has(queryGql)) {
    map.set(queryGql, createSubGql(queryGql))
  }

  var subGql = map.get(queryGql)

  const sub = useSubscription(GET_MESSAGES_SUB, {
    onSubscriptionData({client, subscriptionData: {data}}) {
      client.writeQuery({
        query: query,
        data: data
      })
    }
  })

  return queryResult
}


