import css from 'styled-jsx/css'
import Page from "../Widgets/Page"
import { useState } from "react"
import Button from "../Widgets/Button"
import gql from 'graphql-tag'

import {
  useQuery, useSubscription, useMutation, useApolloClient
} from '@apollo/react-hooks'

import useLiveQuery from "../../lib/useLiveQuery";


const GET_MESSAGES = gql`
  {
    message {
      id
      text
    }
  }
`

const DELETE_MESSAGE = gql`
  mutation ($id: Int!) {
    delete_message_by_pk(id: $id) {
      id
    }
  }
`

const SEND_MESSAGE = gql`
  mutation ($text: String){
    insert_message_one(object: {text: $text}) {
      id text
    }
  }
`

function MessagesInput() {
  const [sendMessage] = useMutation(SEND_MESSAGE)

  function onSubmit(event) {
    if (event.key !== 'Enter') return
    const text = event.target.value
    event.target.value = ''
    sendMessage({
      variables: {text},
      refetchQueries: [{query: GET_MESSAGES}]
    })
  }

  return <input type="text" onKeyDown={ onSubmit }/>
}


function DeleteButton({id}) {
  const [deleteMessage] = useMutation(DELETE_MESSAGE)

  function onClick() {
    deleteMessage({
      variables: {id},
      refetchQueries: [{query: GET_MESSAGES}]
    })
  }

  return <Button onMouseOver={ onClick }>Delete { id }</Button>
}


function MessagesList() {
  const {loading, error, data} = useLiveQuery(GET_MESSAGES)
  if (loading) return <div>Loading...</div>
  if (error) return <div>Error { error.message }...</div>

  return <ul>
    { data.message.map(message => (
      <li key={ message.id }>
        <DeleteButton id={ message.id }/>
        { message.text }
      </li>
    )) }
  </ul>
}

export default function RefsPage() {


  return <Page>
    <div>
      <MessagesInput/>
      <MessagesList/>
    </div>
  </Page>
}
