import css from 'styled-jsx/css'
import Page from "../Widgets/Page"
import { useState } from "react"
import Button from "../Widgets/Button"
import gql from 'graphql-tag'

import {
  useQuery, useSubscription, useMutation, useApolloClient
} from '@apollo/react-hooks'


function useLiveQuery(query) {

}

const GET_MESSAGES_SUB = gql`
  subscription {
    message {
      id
      text
    }
  }
`

const DELETE_MESSAGE = gql`
  mutation ($id: Int!) {
    delete_message_by_pk(id: $id) {
      __typename
    }
  }
`

function DeleteButton({id}) {
  const [deleteMessage, status] = useMutation(DELETE_MESSAGE)
  return <Button onClick={ () => {
    deleteMessage({
      variables: {id: id},
    })
  } }>Delete { id }</Button>
}

export default function RefsPage() {

  const {loading, error, data} = useSubscription(GET_MESSAGES_SUB)
  if (loading || error) return <div>Loading...</div>


  return <Page>
    <ul>
      { data.message.map(message => (
        <li key={ message.id }>
          <DeleteButton id={ message.id }/>
          { message.text }
        </li>
      )) }
    </ul>
  </Page>
}
