import { useQuery, useSubscription } from "@apollo/react-hooks"
import gql from "graphql-tag"


const GET_MESSAGES_SUB = gql`
  subscription {
    message {
      id
      text
    }
  }
`

const map = new WeakMap()

export default function useLiveQuery(query) {
  const queryResult = useQuery(query)

  if (!map.has(query)) {
    console.log('create')
    map.set(query, createSubscription(query))
  }

  const subscription = map.get(query)
  console.log(subscription)

  useSubscription(subscription, {
    onSubscriptionData({client, subscriptionData: {data}}) {
      client.writeQuery({
        query: query,
        data: data
      })
    }
  })

  return queryResult
}


function createSubscription(query) {
  let subscription = JSON.parse(JSON.stringify(query))
  subscription.definitions[0].operation = 'subscription'
  return subscription
}
