import { useQuery, useSubscription } from "@apollo/react-hooks"
import gql from "graphql-tag"
import {default as q} from "graphql-tag"


const GET_MESSAGES_SUB = gql`
  subscription {
    message {
      id
      text
    }
  }
`

const map = new WeakMap()

export default function useLiveQuery(query) {
  const queryResult = useQuery(query)

  if (!map.has(query)) {
    map.set(query, createSubscription(query))
  }

  // var subscription = map.get(query)

  useSubscription(GET_MESSAGES_SUB, {
    onSubscriptionData({client, subscriptionData: {data}}) {
      client.writeQuery({
        query: query,
        data: data
      })
    }
  })

  return queryResult
}


function createSubscription(query) {
  console.log(gql)
  console.log('create', q)
}
